```kotlin
class Solution {

    data class Interval(val from: Int, val to: Int)

    fun minInterval(intervals: Array<IntArray>, queries: IntArray): IntArray {

        val queriesWithIndex = Array<Pair<Int, Int>>(queries.size) { queries[it] to it }
        queriesWithIndex.sortBy { it.first }

        val intervalsByFrom = Array<Interval>(intervals.size) { Interval(intervals[it][0], intervals[it][1]) }
        intervalsByFrom.sortBy { it.from }

        // by size
        val intervalMinHeap = PriorityQueue<Interval> { f, s -> (f.to - f.from) - (s.to - s.from) }

        val result = IntArray(queries.size)

        var j = 0
        for ((queryVal, queryIndex) in queriesWithIndex) {

            while (j < intervalsByFrom.size && intervalsByFrom[j].from <= queryVal) {
                intervalMinHeap.add(intervalsByFrom[j])
                j++
            }

            while (!intervalMinHeap.isEmpty() && intervalMinHeap.first().to < queryVal) {
                intervalMinHeap.remove();
            }
            result[queryIndex] =
                if (intervalMinHeap.isEmpty())
                    -1
                else
                    (intervalMinHeap.first().to - intervalMinHeap.first().from + 1)

        }
        return result
    }
}
