```csharp
public class Solution {
    public int[] MinInterval(int[][] intervals, int[] queries) {
        intervals = intervals.OrderBy(x => x[0]).ToArray();
        Dictionary<int, int> resultDict = new(); // key = 'query', value = 'interval size'
        PriorityQueue<(int size, int right), int> pq = new();

        int i = 0;
        foreach (int q in queries.OrderBy(x => x)) { // order queries, but don't change original array, it will be used at the end to return sized in correct order

            while (i < intervals.Length && intervals[i][0] <= q) { // if there still invtervals and interval starts before current query
                int l = intervals[i][0], r = intervals[i][1];
                int size = r - l + 1;
                pq.Enqueue((size, r), size);
                i++;
            }

            // pop those which are no longer valid
            while (pq.Count > 0 && pq.Peek().right < q) {
                pq.Dequeue();
            }
            resultDict[q] = pq.Count > 0 ? pq.Peek().size : -1; // even if there are duplicates, they will be overriden with the same value
        }
        return queries.Select(q => resultDict[q]).ToArray();
