    void update(int l,int r,int i,int j,int sv,int val){
        propogate(l,r,sv);
        if(r<i or l>j) return ;
        if(l>=i and r<=j){
            lazy[sv]=min(lazy[sv],val);
            propogate(l,r,sv);
            return ;
        }
        int m=(l+r)/2;
       update(l,m,i,j,sv*2,val);
        update(m+1,r,i,j,sv*2+1,val);
        seg[sv]=min(seg[sv],min(seg[sv*2],seg[sv*2+1]));
    }

    int find(int l,int r,int v,int sv){
        propogate(l,r,sv);

        if(l==r){

            return seg[sv] ;
        }
        int m=(l+r)/2;
        if(m>=v) return find(l,m,v,sv*2);
        return find(m+1,r,v,sv*2+1);

