class Solution {
	public int[] minInterval(int[][] intervals, int[] queries) {
		Arrays.sort(intervals,(x,y)->{
			if(x[1]!=y[1]) 
				return y[1]-x[1];
			return y[0]-x[0];
		});
		int[][] queryWithIndex = new int[queries.length][2];
		for(int i=0;i<queryWithIndex.length;i++){
			queryWithIndex[i][0] = queries[i];
			queryWithIndex[i][1] = i;
		}
		Arrays.sort(queryWithIndex,(x,y)->y[0]-x[0]);
		//minHeap int[0] : distance,int[1] : left
		PriorityQueue<int[]> minHeap = new PriorityQueue<>((x,y)->x[0]-y[0]);

		int[] result = new int[queries.length];
		for(int i=0,j=0;i<queryWithIndex.length;i++){
			int query  = queryWithIndex[i][0];
			int queryIndex = queryWithIndex[i][1];
			while(j<intervals.length && intervals[j][1]>=query){
				minHeap.offer(new int[]{intervals[j][1]-intervals[j][0]+1,intervals[j][0]});
				j++;
			}
			while(minHeap.size()>0 && minHeap.peek()[1]>query){
				minHeap.poll();
			}
			if(minHeap.size()==0)
				result[queryIndex] = -1;
			else
				result[queryIndex] = minHeap.peek()[0];
		}
		return result;
	}
}

