class Solution {
    public int[] minInterval(int[][] intervals, int[] queries) {

        int[][] index_query = new int[queries.length][2];
        for(int i = 0;i<index_query.length;i++){
            index_query[i][0] = queries[i];
            index_query[i][1] = i;
        }

        // sort queries as per the 
        Arrays.sort(index_query, new Comparator<int[]>(){
            public int compare(int[] a, int[] b){
                if(a[0] == b[0])
                    return a[1] - b[1];
                return a[0] - b[0];
            }
        });

        // sort the intervals as per starting time
        Arrays.sort(intervals, new Comparator<int[]>(){
            public int compare(int[] a, int[] b){
                if(a[0] == b[0])
                    return a[1] - b[1];
                return a[0] - b[0];
            }
        });

        // make the priority queue
        PriorityQueue<int[]> ret = new PriorityQueue<>(new Comparator<int[]>(){
            public int compare(int[] a, int[] b){
                if(a[0] == b[0])
                    return a[1] - b[1];
                return a[0] - b[0];
            }
        });

        // iterate over the queries for the results
        int[] fret = new int[queries.length];
        int j = 0;
        for(int i = 0;i<index_query.length;i++){
            while(j < intervals.length){
                if(intervals[j][0] <= index_query[i][0]){
                    // add to the priority queue
                    ret.add(new int[]{intervals[j][1] - intervals[j][0]+1, intervals[j][1]});
                    j += 1;
                }
                else
                    break;
            }

            // now, weed out those intervals whose end time is less than query
            while(!ret.isEmpty()){
                int[] temp = ret.peek();
                if(temp[1] < index_query[i][0])
                    ret.poll();
                else
                    break;
            }
            if(ret.isEmpty())
                fret[index_query[i][1]] = -1;
            else{
                int[] temp = ret.peek();
                fret[index_query[i][1]] = temp[0];
            }
        }
        return fret;
    }
}
