from heapq import *
from collections import defaultdict


class Solution:
    def minInterval(self, intervals, queries):
        intervals=list(set(list(map(tuple,intervals))))
        L=defaultdict(list)
        for i,(l,r) in enumerate(intervals):
            L[l].append((r-l+1,r,i))
        ma=max(max(e) for e in intervals)
        answers=[-1 for _ in range(ma+1)]
        patch=set([e[0] for e in intervals if e[0]==e[1]])
        cur=[]
        for i in sorted(queries+L.keys()):
            for l in L[i]:
                heappush(cur,l)
            if i in patch:
                answers[i]=1
                continue
            got=False
            if len(cur):
                d,r,j=cur[0] #peek top
                got=True
            while len(cur) and r<i:
                d,r,j=heappop(cur)
                got=True
                if len(cur) and cur[0][1]>=i:
                    d,r,j=cur[0] #peek top
                    break
            if got and r>=i:
                answers[i]=d
        result=[]
        for q in queries:
            if q>ma:
                result.append(-1)
            else:
                result.append(answers[q])
        return result
