class Solution {
public:
    int res = 0;

    void traverse(TreeNode* node, string sum) {
        if (!node) return;

        sum = sum + to_string(node->val);

        if (!node->left && !node->right) res += stoi(sum, nullptr, 2);

        traverse(node->left, sum);
        traverse(node->right, sum); 
    }

    int sumRootToLeaf(TreeNode* root) {
        traverse(root, "");
        return res;
    }
};
```
