    private void Helper(TreeNode node, ref int sum, int current)
    {
        if (node == null)
        {
            return;
        }

        current *= 2;
        current += node.val;

        if (node.left == null && node.right == null)
        {
            sum += current;
            return;
        }

        Helper(node.left, ref sum, current);
        Helper(node.right, ref sum, current);
    }

    public int SumRootToLeaf(TreeNode root)
    {
        int sum = 0;
        Helper(root, ref sum, 0);
        return sum;
    }
