class Solution {
    public int sumRootToLeaf(TreeNode root) {
        if (root == null) {
            return 0;
        }

        int sum = 0;

        Queue<Pair<TreeNode, Integer>> q = new LinkedList<>();
        q.add(new Pair(root, root.val));
        int newValue = 0;
        while (!q.isEmpty()) {
            Pair<TreeNode, Integer> current = q.poll();
            TreeNode node = current.getKey();
            int value = current.getValue();
            if (node.left == null && node.right == null) {
                sum = sum + value;
                continue;
            }

            if (node.left != null) {
                newValue = (value << 1) + node.left.val;
                q.add(new Pair(node.left, newValue));
            }

            if (node.right != null) {
                newValue = (value << 1) + node.right.val;
                q.add(new Pair(node.right, newValue));
            }            
        }

        return sum;
    }
}
```

