class Solution:
    def sumRootToLeaf(self, root: TreeNode) -> int:

        def fn(node): 
            """Pre-order traverse the tree and add root-to-leaf numbers."""
            nonlocal ans
            if not node: return 
            stack.append(node.val)
            if node.left is node.right is None: ans += int("".join(map(str, stack)), 2)
            fn(node.left) or fn(node.right)
            stack.pop()

        ans, stack = 0, []
        fn(root)
        return ans 
