    void bin(string s) {
        int ans = 0;
        int n = s.size() - 1;
        for (auto i: s) {
            ans += (i - 48) * pow(2, n);
            n--;
        }
        sum += ans;
    }

    void func(TreeNode *root, string s) {
        if (root == nullptr) {
            return bin(s);
        }
        s += std::to_string(root->val);
        if (root->right == nullptr && root->left == nullptr){
            return bin(s);
        } else {
            if (root->left == nullptr || root->right == nullptr){
                if (root->left != nullptr){
                    return func(root->left, s);
                } else {
                    return func(root->right, s);
                }
            } else {
                func(root->left, s);
                func(root->right, s);
            }
        }

    }

    int sumRootToLeaf(TreeNode *root) {
        func(root, "");
        return sum;
    }
};

