/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    int sumRootToLeaf(TreeNode* root) {
        {
           return sum(root,0); 
        }
    }
    int sum(TreeNode* root ,int till)
    {  
        till=till+root->val;
        if(!root->left && !root->right)
        {
            return till;
        }
        if(root->left && root->right)
        {
            return sum(root->left,2*till)+sum(root->right,2*till);
        }
        if(root->right)
            return sum(root->right,2*till);
        if(root->left)
            return sum(root->left,2*till);
        return 0;
    }
};

