```
class Solution {
public:
    int minDays(vector<int>& bloomDay, int m, int k) {
        const int N = bloomDay.size();
        if (m*k > N) return -1; //not enough flowers for the given requirement

        int low = 1, high = INT_MIN, ans = -1;
        for (const auto &n : bloomDay) high = max(high, n);

        while (low <= high) { //try possible bloom days in range [1, max(bloomDay..)]
            int mid = low + (high - low)/2; 
            int cnt = 0, parts = 0; //cnt = continuous valid flowers for bouquets, parts = no. of such valid partitions 
            for (int i = 0; i < N; ++i) {
                if (bloomDay[i] <= mid) cnt++;
                else cnt = 0;
                if (cnt == k) {
                    parts++;
                    cnt = 0;
                }
            }
            if (parts < m) {
                low = mid + 1;
            } else {
                ans = mid;
                high = mid - 1;
            }
        }

        return ans;
    }
};
```

