class Solution:
    def minDays(self, bloomDay: List[int], m: int, k: int) -> int:
        n = len(bloomDay)
        val = m * k
        if val > n:
            return -1

        low = float('inf')
        high = float('-inf')
        for day in bloomDay:
            low = min(day, low)
            high = max(day, high)

        while low <= high:
            mid = low + (high - low) // 2
            if self.possible(bloomDay, mid, m, k):
                high = mid - 1
            else:
                low = mid + 1

        return low

    def possible(self, bloomDay, day, m, k):
        cnt = 0
        num = 0
        for bd in bloomDay:
            if bd <= day:
                cnt += 1
            else:
                num += cnt // k
                cnt = 0
        num += cnt // k
        return num >= m

```
```JAVA []
class Solution {
    public int minDays(int[] bloomDay, int m, int k) {
        int n = bloomDay.length;
        long val = (long) m * k;
        if (val > n) return -1;

        int low = Integer.MAX_VALUE, high = Integer.MIN_VALUE;
        for (int day : bloomDay) {
            low = Math.min(day, low);
            high = Math.max(day, high);
        }

        while (low <= high) {
            int mid = low + (high - low) / 2;
            if (possible(bloomDay, mid, m, k)) {
                high = mid - 1;
            } else {
                low = mid + 1;
            }
        }

        return low;
    }

    private boolean possible(int[] bloomDay, int day, int m, int k) {
        int cnt = 0, num = 0;
        for (int i = 0; i < bloomDay.length; i++) {
            if (bloomDay[i] <= day) {
                cnt++;
            } else {
                num += cnt / k;
                cnt = 0;
            }
        }
        num += cnt / k;
        return num >= m;
    }
}

