# Code
```
class Solution {
public:
    int minDays(vector<int>& bloomDay, int m, int k) {
        if (bloomDay.size() < (long long) m * k) 
            return -1;

        int mn = INT_MAX, mx = INT_MIN;
        for (int i : bloomDay) {
            mx = max(mx, i);
            mn = min(mn, i);
        }

        int l = mn; 
        int r = mx;

        // l, r bloom days (min, max)
        while (l < r) {
            int mid = (l + r) / 2;
            int possibleBookies = getPossibleBookies(bloomDay, mid, k);

            // still not enough.  If wait more days, we will get more floor.  Now increase l (wait more)
            if (possibleBookies < m) 
                l = mid + 1;
            else  // why not mid - 1?
                r = mid;
        }

        return l;      
    }

    int getPossibleBookies(vector<int>& bloomDay, int day, int k) {
        int bouquets = 0, flowersCollected = 0;

        for (int value : bloomDay) {
            if (value <= day) {
                flowersCollected++;
            } 
            else  // k adjacent flowers.  if value > day, it will bloom value days. now 0.
                flowersCollected = 0;

            if (flowersCollected == k) {
                // collect all k adjacent flowers. Now collect another k adjacent flowers
                bouquets++;
                flowersCollected = 0;
            }
        }
        return bouquets;
    }
};
```
