        int n = arr.size();
        int r=*max_element(arr.begin(), arr.end());
        if(m*k>n) return -1;
        int l=*min_element(arr.begin(), arr.end());
        int ans=-1;
        while(l<=r) {
            int mid = l + (r-l)/2;
            if(check(arr, m, k, mid)) {
                r=mid-1;
                ans=mid;
            }
            else l=mid+1;
        }
        return ans;
    }
};
