const BLOOMED = 'X';
/**
 * @param {number[]} bloomDay
 * @param {number} m
 * @param {number} k
 * @return {number}
 */
var minDays = function(bloomDay, m, k) {
    if ((m * k) > bloomDay.length) return -1;

    const unionFind = bloomDay.map((_, idx) => idx);
    const daysAndIndexes = bloomDay.map((day, idx) => ({ day, idx }));
    daysAndIndexes.sort((a,b) => a.day - b.day);


    if (k === 1) return daysAndIndexes[m - 1].day;

    const find = (x) => unionFind[x] = unionFind[x] === x ? x : find(unionFind[x]);
    const union = (x, y) => unionFind[find(x)] = find(y);

    const map = {};
    let totalBouquets = 0;
    for (let { day, idx } of daysAndIndexes) {
        bloomDay[idx] = BLOOMED;
        map[idx] = { numFlowers: 1, numBouquetsInGroup: 0 };

        let totalPrevBouquets = 0;
        for (let delta of [1, -1]) {
            if (bloomDay[idx + delta] === BLOOMED) {
                const root = find(idx + delta);
                union(root, idx);
                map[idx].numFlowers += map[root].numFlowers;
                totalPrevBouquets += map[root].numBouquetsInGroup;
            }
        }

        map[idx].numBouquetsInGroup = Math.floor(map[idx].numFlowers / k);
        if (map[idx].numBouquetsInGroup > totalPrevBouquets) totalBouquets++;

        if (totalBouquets === m) return day;
    }
};
