class Solution {
public:
    int flower(vector<int>& b, int day, int k) {
        int flow = 0;
        int count = 0;
        for (int i = 0; i < b.size(); i++) {
            if (b[i] <= day) {
                count++;
                if (count == k) {
                    flow++;
                    count = 0; // Reset count after reaching k consecutive flowers
                }
            } else {
                count = 0;
            }
        }
        return flow;
    }

    int minDays(vector<int>& bloomDay, int m, int k) {
        if (m > bloomDay.size() / k) return -1;
        int low = INT_MAX;
        int high = INT_MIN;
        for (auto i : bloomDay) {
            low = min(low, i);
            high = max(high, i);
        }
        while (low <= high) {
            int mid = low + (high - low) / 2; // Avoid potential integer overflow
            if (flower(bloomDay, mid, k) >= m) {
                high = mid - 1;
            } else {
                low = mid + 1;
            }
        }
        return low;
    }
};

