class Solution
{
public:
    int minDays(vector<int> &bloomDay, int m, int k)
    {

        int n = bloomDay.size();

        if ((m * k) > n)
        { // if there are no flowers which are required then we simply return -1
            return -1;
        }

        int low = INT_MAX;

        int high = INT_MIN;

        for (auto d : bloomDay)
        {

            low = min(low, d);

            high = max(high, d);
        }

        while (low < high)
        {

            int mid = low + (high - low) / 2;

            int cur = 0, adj = 0;

            for (int i = 0; i < n; i++)
            {
                if (bloomDay[i] > mid)
                    adj = 0; // check if the flower is bloomed or not

                else
                {
                    adj++; // if the criteria for k is complete or not

                    if (adj == k)
                    {
                        cur++;
                        adj = 0;
                    }
