	class Solution {
	public:
		bool isValid(vector<int>& bloomDay, int bouquets, int flowers, int days){
			int bouq = 0, flow = 0; //initially keep the bouqets with flowers that can be formed as 0
			for(auto bloom : bloomDay){
				flow = (bloom <= days) ? flow + 1 : 0;  //Increment flowers if element is less than or equal to days , or else reset it to 0
				if(flow == flowers){  //if flowers == desired flowers , you can make a bouquet, Reset flowers after incrementing bouquet
					flow = 0; 
					bouq ++;
				}
			}
			return bouq >= bouquets; //return true if bouqets >= desired bouqets or return false
		}

    int minDays(vector<int>& bloomDay, int m, int k) {
        if(bloomDay.size() < m*k) return -1; // in case making bouqet isnt possible because that many flowers arent present
        int low = 1; //Search space - 1 to max of arr. (1 day to bloom or max of all the days present to bloom)
        int high = 0;
        for(auto days: bloomDay) high = max(high, days);
        while(low < high){
            int mid = low + (high-low)/2; //avoid integer overflow
            if(isValid(bloomDay, m, k, mid)){
                high = mid; //if the isValid returns true, try to find a better result by shrink the search space to the left.(Note that mid can be a possible answer , hence high = mid and not mid-1)
            }else{
                low = mid + 1; //if isValid returns false, eliminate left. Hence low = mid + 1 and not low = mid
            }
        }
        return low; //at last low and high will be pointing to the same answer, so return anyone of them
    }
};

