class Solution {
    public int minDays(int[] bloomDay, int m, int k) {
        if(bloomDay.length < m*k){return -1;}

        int left = 1;
        int max = Integer.MIN_VALUE;
        for(int i : bloomDay){
            max = Math.max(i, max);
        }
        int right = max;
        while(left < right){
            int mid = left + (right-left)/2;
            if(feasible(bloomDay, m, k, mid)){
                right = mid;
            }else{
                left = mid + 1;
            }
        }

        return left;
    }

    public boolean feasible(int[] bloomDay, int m, int k, int days){
        int count_continuous = 0;
        int count_flower = 0;
        for(int i : bloomDay){
            if(i <= days){
                count_continuous ++;
                if(count_continuous >= k){
                    count_continuous = 0;
                    count_flower ++;
                }
            }else{
                count_continuous = 0;
            }
        }

        return count_flower >= m;
    }
}
