    bool makeBouquet(vector<int>& bloomDay, int m, int k,int mid){
        int flower=0;
        int n=bloomDay.size();
        int bouquet=0;
        for(int i=0;i<n;i++){
            if(bloomDay[i]>mid){
                flower=0;
            }else{
                flower++;
            }

            if(flower>=k){
                bouquet++;
                flower=0;
            }
        }

        return (bouquet>=m);
