class Solution {
    public int minDays(int[] bloomDay, int m, int k){
        int low = Integer.MAX_VALUE; int high = Integer.MIN_VALUE;
        for(int d : bloomDay){
            low = Math.min(low, d);
            high = Math.max(high, d);
        }

        int result = -1;

        while(low <= high){
            int mid = low + (high - low)/2;

            if(isSufficient(mid, bloomDay, m, k)){ // Check if (days = mid) will be sufficient to make bouquets
                result = mid;
                high = mid - 1;
            }
            else { // If not sufficient then go to right side to check with greater number of days
                low = mid + 1;
            }
        }
        return result;
    }

    public boolean isSufficient(int day, int[] bloomDay, int m, int k){
        int result = 0; int counter = 0;

        for(int i = 0; i < bloomDay.length; i++){
            if(bloomDay[i] <= day && counter < k){
                counter++;
            }
            else if(bloomDay[i] > day && counter > 0){
                counter = 0;
            }

            if(counter >= k){
                result++;
                counter = 0;
            }
        }
        return result >= m;
    }
}
