class Solution {

    Integer[][] dp;
    HashMap<Integer, Integer> bloom = new HashMap<>();
	// days required for bouquet [start, start+k-1]

    public int minDays(int[] bloomDay, int m, int k) {
        int n = bloomDay.length;
        Integer[] days = new Integer[bloomDay.length];
        for(int i = 0; i < n; i++){
            days[i] = bloomDay[i];
        }


        PriorityQueue<Integer> pq = new PriorityQueue<>((a,b)->b-a);
        for(int i = 0; i < k; i++){
            pq.add(days[i]);
        }
        bloom.put(0, pq.peek());

        for(int i = k; i < n; i++){
            pq.remove(days[i-k]);
            pq.add(days[i]);
            bloom.put(i-k+1, pq.peek());
        }
        dp = new Integer [m+1][n+1];
        int ans = dfs(m, k, 0);
        if(ans == Integer.MAX_VALUE) return -1;
        return ans;
    }

    int dfs(int m, int k, int flowerIdx){
         if(m == 0)
            return 0;

         if(bloom.get(flowerIdx) == null) 
             return Integer.MAX_VALUE;

         if(dp[m][flowerIdx] != null)
             return dp[m][flowerIdx];

         int localMin = Math.min(
         //take
         Math.max(dfs(m-1, k, flowerIdx+k), bloom.get(flowerIdx)), 
         //no take
         dfs(m, k, flowerIdx+1));

         dp[m][flowerIdx] = localMin;
         return localMin;
    }
}

