class Solution {
public:
	bool isPos(int x, int m, int k, vector<int> &b) {
		int cur = 0;
		int tot = 0;
		for (int &i : b) {
			if (i > x) tot += cur / k, cur = 0;
			else cur++;
		}
		return tot >= m;
	}

	int minDays(vector<int>& b, int m, int k) {
		if (b.size() / k < m) return -1;
		b.push_back(INT_MAX);

		int mi = 0, ma = INT_MAX;
		while (mi + 1 < ma) {
			int mid = mi + (ma - mi) / 2;
			if (isPos(mid, m, k, b)) ma = mid;
			else mi = mid;
		}

		return ma;
	}
};
