class Solution {

	public int minDays(int[] bloomDay, int m, int k) {

		// If No. of flowers required are less than no. of flowers to bloom, then bouquet impossible
        if(bloomDay.length < m*k)
            return -1;

        int left = 1, right = findMax(bloomDay); // right is set to last day a flower blooms

        while(left <= right)  {

            int mid = left + (right - left)/2;

			// if bouquets possible on day i, then right = i - 1
            if(isPossible(bloomDay, mid, m, k)) {
                right = mid - 1;
            } else {
                left = mid + 1;
            }

        }

        return left;    


    }

    private boolean isPossible(int[] bloomDay, int day, int m, int k) {

        int bouquets = 0; // No. of bouquets
        int countAdjacent = 0; // Adjacent flowers counter

        for(int blooms : bloomDay) {

		   // if flower has bloomed, increment countAdjacent
		   if(blooms <= day) {
                countAdjacent++;

				// When k adjacent flowers bloomed, increment bouquets
				if(countAdjacent == k) {
                    bouquets++;
                    countAdjacent = 0; // reset countAdjacent
                }

		   } else {
                countAdjacent = 0; // If found a gap in adjacent flowers, reset countAdjacent
            }
        }

        return bouquets >= m; // if no. of bouquets possible >= required bouquets
    }

	// Find the day the last flower blooms
    private int findMax(int[] bloomDay) {
        int max = Integer.MIN_VALUE;
        for(int day : bloomDay) {
            if(max < day)
                max = day;
        }
        return max;
    }
}

