public:
    int minDays(vector<int>& nums, int m, int k)
    {
        int n=nums.size();
        if(n<m*k)
            return -1;
        int l=nums[min_element(nums.begin(),nums.end())-nums.begin()];
        int r=nums[max_element(nums.begin(),nums.end())-nums.begin()];
        while(l<r)
        {
            int mid=(r-l)/2+l;
            int z=0,cnt=0;
            for(int i=0;i<n;i++)
