			s=c=0;
			for (int i=0;i<l;++i)
				if (a[i]<=t)c+=b[i];
				else s+=c/k,c=0;
			s+=c/k;
			if (s>=m){
				ans=t;
				for (int i=0;i<l;++i)if (a[i]>=t)a[i]=inf;
			}
			else {
				for (int i=0;i<l;++i)if (a[i]<=t)a[i]=0;
			}
			l1=0;
			for (int i=1;i<l;++i)
				if (a[i]==a[l1])b[l1]+=b[i];
				else {
					++l1,a[l1]=a[i],b[l1]=b[i];
					if (a[l1]==inf&&a[l1-1]==0&&l1>=2&&a[l1-2]==inf)m-=b[l1-1]/k,l1-=2;
				}
			l=l1+1;
			if (l<4){
				l1=0;
				for (int i=0;i<l;++i)if (a[i]>0&&a[i]<inf)l1=1;
				if (!l1)return ans;
			}
		}
    }
};
```

