class Solution {    // Binary Search, O(nlog(max(bloomDay))) -> O(nlogn)
public:

    bool isPossible( vector<int>& bloomDay, int boqCnt, int flrCnt, int curDay ){

        int adjCnt = 0;                 // count for number of adjacent bloomed flower we found

        for( auto bDay : bloomDay ){    // iterate for all the flowers in bloomDay

            if( bDay <= curDay )        // if the bloom day is smaller than current day, this means that the flower is bloomed, so increase adjacent count
                adjCnt++ ;

            else adjCnt = 0;            // if the flower is not bloomed, reset adjacent count

            if( adjCnt == flrCnt ){     // if the adjacent count becomes equal to the flower needed to make a single boquet
                boqCnt-- ;              // decrease the boquet count as we made one boquet
                adjCnt = 0 ;            // reset the adjacent count, as we will find new k adjacent flowers now
            }    
        }

        if( boqCnt <= 0 ) return true;

    return false;
}

    int minDays(vector<int>& bloomDay, int m, int k) {

        // if the flowers needed to make all the m boquets that is m times k is greater than the provided flowers, its not possible to make the boquets, return -1
        if( (long long )m * k > bloomDay.size() ) return -1;   

        int minDay = 1;          // minimum days for a flower to bloom, atleast 1 ofcourse
        int maxDay = 1000000000; // maximum day possible, according to constraints let it be 1e9, or we can use max(bloomDay)

        while( minDay < maxDay ){   // while the minDay is smaller than maxDay

            int curDay = minDay + ( maxDay - minDay )/2;  // let the curDay be the middle between minDay and maxDay

            // if for the current day, we can make the m boquets, then lets try for a previous day, so shrink the search space starting from minDay to curDay
            if( isPossible( bloomDay, m, k, curDay ) )      
                maxDay = curDay ;

            // if we cant make m boquets at current day, lets try for a latter day, shrink the search space from, curDay to maxDay
            else minDay = curDay + 1; 

        }
    // return the minimum day we found 
    return minDay;
    }
};
```

