public class Solution {
    public int MinDays(int[] bloomDay, int m, int k) {
        var n = bloomDay.Length;

        if (n < m * k) {
            return -1;
        }

        var left = 1;
        var right = 1000000000;
        var res = Int32.MaxValue;

        while (left <= right) {
            var mid = left + (right - left) / 2;

            var flowers = 0;
            var bouquets = 0;
            for (var i = 0; i < n; i++) {
                if (bloomDay[i] > mid) {
                    flowers = 0;
                    continue;
                } 

                flowers++;
                if (flowers == k) {
                    flowers = 0;
                    bouquets++;
                    if (bouquets == m) {
                        res = Math.Min(res, mid);
                        break;
                    }
                }                
            }

            if (bouquets >= m) {
                right = mid - 1;
            } else {
                left = mid + 1;
            }
        }

        return res == Int32.MaxValue ? -1 : res;
    }
}
```

