class Solution {
    bool isPos(vector<int>& bloomDay, int day, int m, int k) {
	//Method returns true if it is possible to satisfy given requirement
        int n=bloomDay.size(), i=-1, j=0, numBq=0;    //i is point before start of window, j is point under examination
        while(j<n) {
            if(bloomDay[j]<=day) {
			//If the flower has bloomed
                if(j-i==k) {
                    numBq++;
                    i=j;
                }
                j++;
            }
            else {
			//Restart the window
                i = j;
                j++;
            }
        }
        return numBq>=m;
    }

public:
    int minDays(vector<int>& bloomDay, int m, int k) {
        int n = bloomDay.size();

        if(n < m * k)
            return -1;

        int left = 1, right = *max_element(bloomDay.begin(), bloomDay.end()), mid;
        while(left < right) {
            mid = left + (right - left)/2;

            if(isPos(bloomDay, mid, m, k)) 
                right = mid;
            else
                left = mid + 1;
        }

        return left;
    }
};
