class Solution extends PredicateAPI {
    public int minDays(int[] bloomDay, int m, int k) {
        if(bloomDay.length < m * 1L * k) return -1;
        return minimumDaysToMakeMBouquets(bloomDay, m, k);
    }

    private int minimumDaysToMakeMBouquets(int[] bloomDay, int m, int k) {
        int maxBloomDay = Arrays.stream(bloomDay).max().getAsInt(); 
        int left = 1, right = maxBloomDay;
        while(left <= right) {
            int mid = left + (right - left) / 2;
            if(isPossibleToMakeMBouquetsWithinDDays(bloomDay, m, k, mid))
                right = mid - 1;
            else
                left = mid + 1;
        }
        return left;
    }
}

class PredicateAPI {
    private int[] hasFoundKAdjacentFlowers(int[] bloomDay, int d, int left, int right) {
        while(left < right) {
            if(bloomDay[left] > d)
                return new int[]{0, left};
            left += 1;
        }
        return new int[]{1, right};
    }

    boolean isPossibleToMakeMBouquetsWithinDDays(int[] bloomDay, int m, int k, int d) {
        int lastWinStart = bloomDay.length - k;
        int left = 0, right = k;
        while(left <= lastWinStart) {
            int[] found = hasFoundKAdjacentFlowers(bloomDay, d, left, right);
            if(found[0] == 1) m -= 1;
            left = (found[0] == 1) ? found[1] : found[1] + 1;
            right = left + k;
            if(m == 0) return true;
        }
        return false;
    }
}
