class Solution {
    public int minDays(int[] bloomDay, int m, int k) {
        int s = min(bloomDay), e = (int)1e9, ans = -1;
        while(s <= e) {
            int mid = s + (e - s) / 2;
            if(completable(bloomDay, mid, m, k)) {
                ans = mid;
                e = mid - 1;
            } else s = mid + 1;
        }
        return ans;
