Time Complexity - O(n * n * log(max item in bloomDay)) 

```
class Solution {
    public int minDays(int[] bloomDay, int m, int k) {
        int low = 1;
        int high = Integer.MIN_VALUE;

        for (int day : bloomDay) {
            high = Math.max(day, high);
        }

        boolean flag = false;

        while (low <= high) {
            int mid = (low + high)/2;

            if (isPossible(bloomDay, m, k, mid)) {
                flag = true;
                high = mid - 1;
            } else
                low = mid + 1;
        }

        return flag ? low : -1;
    }

    private boolean isPossible(int[] bloomDay, int m, int k, int day) {

        for (int i=0; i < bloomDay.length - k + 1; i++) {
            int count = 0;
            for (int j = i; j < i + k; j++) {
                if (bloomDay[j] <= day)
                    count++;
                else 
                    break;
            }

            if (count == k) {
                m--;
                i = i + k - 1;
            }
        }

        return m <= 0;
    }
}
