	def isValid(day):
		i = 0
		c = 0
		temp = 0
		while(i < n):
			if(bloomDay[i] <= day):
				c += 1
			else:
				temp += c//k
				c = 0
			if(temp >= m):
				return True
			i += 1
		temp += c//k
