    let right = 0;

    for (const day of bloomDay) {
        left = Math.min(day, left);
        right = Math.max(day, right);
    }

    let ans = right;

    while (left < right) {
        const day = Math.floor((left + right) / 2);

        let count = 0;
        let current = 0;

        for (let j = 0; j < bloomDay.length; j++) {
            if (bloomDay[j] <= day) {
