public:
    bool makeBoquet(vector<int>& bloomDay, int& m, int& k, int& mid){
        int count = 0;
        int adjacent = 0;

        for(auto &flower : bloomDay){
            if(flower > mid){ // flower doesnt bloom yet
                adjacent = 0;
            }else{
                ++adjacent;
                if(adjacent == k){
                    count++;
                    adjacent = 0;
                }
            }
            if(count == m){ // we already made m boquets
                break;
            }
        }
        return count>=m;
    }

