class Solution {
    public int minDays(int[] bloomDay, int m, int k) {
        if(m*k>bloomDay.length)
            return -1;

        int low=bloomDay[0],high=bloomDay[0];

        for(int x : bloomDay){
            low=Math.min(low,x);
            high=Math.max(high,x);
        }

        while(low<=high){
            int mid=low+(high-low)/2;

            int bouqetsPossible=search(bloomDay,mid,k);

            if(bouqetsPossible<m)
                low=mid+1;
            else
                high=mid-1;
        }
        return low; 
    }

    int search(int[] ar,int val,int k){
        int res=0;
        int i=0,n=ar.length;

        while(i<n){
            int j=i;
            int cnt=0;
            while(j<i+k && j<n){
                if(ar[j]<=val)
                    cnt++;
                else
                    break;
                j++;
            }
            if(cnt==k)
            {
                res++;
                i=j;
            }
            else
                i=j+1;
        } 
        return res;
    }
}
