    public boolean isPossible(int[] arr,int days,int m,int k){
        int len = arr.length;
        int start=0,end=0;
        for(;end<len;end++){
            if(arr[end]>days){
                m-=(end-start)/k;
                start=end+1;
            }
        }
        m-=(end-start)/k;
        return m<=0;
    }
}
