// count(vector<int>& bloomDay, int d, int k) returns the number of bouquet that can be made if we take 'd' days as the min number of days.
// count(bloomDay, d, k) >= m, means that we can easily make 'm' bouquets if we take 'd' as our minimum number of days.
// so we store d in a variable 'res' and continued our binary search to find an even better answer if it exits !

class Solution {
public:
    int minDays(vector<int>& bloomDay, int m, int k) {
        int n = bloomDay.size();
		if(m * k > n) return -1;

        int l = INT_MAX, r = -1;
        for(int i=0; i<n; i++){
            l = min(l, bloomDay[i]);
            r = max(r, bloomDay[i]);
        }

        int res = r;
        while(l <= r){
            int d = l + (r - l)/2;
            if(count(bloomDay, d, k) >= m){
                res = d, r = d-1;
            }else{
                l = d+1;
            }
        }

        return res;
    }

	int count(vector<int>& bloomDay, int d, int k){
        int kcnt = 0, mcnt = 0;
        for(int i=0; i<bloomDay.size(); i++){
            if(d >= bloomDay[i]){
                kcnt++;
                if(kcnt == k){
                    mcnt++;
                    kcnt = 0;
                }
            }else{
                kcnt = 0;
            }
        }
        return mcnt;
    }	

};
