class Solution {
public:
    int len;
    int minDays(vector<int>& bloomDay, int m, int k) 
    {
        len = bloomDay.size();
        if(len/k < m)
            return -1;
        int low=1,high=*max_element(bloomDay.begin(),bloomDay.end());
        int res = -1;
        while(low<=high)
        {
            int mid = low+(high-low)/2;
            if(check(bloomDay,mid,m,k))
            {
                res = mid;
                high = mid-1;
            }
            else
                low = mid+1;
        }
        return res;
    }

    bool check(const vector<int>&bloomDay,int cdays,int m,int k)
    {
        int total_count = 0, current_count = 0;
        for(auto &x : bloomDay)
        {
            if(x<=cdays)
            {
                ++current_count;
                if(current_count==k)
                {
                    ++total_count;
                    current_count = 0;
                }
            }
            else
            {
                current_count = 0;
            }
        }
        return total_count >= m;
    }
};

