        n=len(colors)
        graph=defaultdict(list)
        indegree=defaultdict(int)

        for u,v in edges:
            graph[u].append(v)
            indegree[v]+=1

        queue=[]    
        dp=[[0]*26 for _ in range(n)]
        colorvalues=[ord(c)-ord("a") for c in colors]
        for u in range(n):
            if u not in indegree:
                queue.append(u)
