class Solution {
public:
	int n;
	string s;
	vector<int> used;
	vector<vector<int>> graph,dp;

	bool chkCycle(int i) {
		if(used[i]==2) return 0;
		if(used[i]) return 1;
		used[i]=1;
		for(int j: graph[i]) {
			if(chkCycle(j)) return 1;
		}
		used[i]=2;
		return 0;
	}

	int getDp(int i,int j) {
		if(dp[i][j]!=-1) return dp[i][j];
		int mx=0;
		for(int k: graph[i]) mx= max(mx, getDp(k,j) );
		if(s[i]-'a'==j) mx++;
		dp[i][j]=mx;
		return mx;
	}

	int largestPathValue(string colors, vector<vector<int>>& edges) {
		n=0; s= colors;
		for(auto &i: edges) n=max(n,max(i[0],i[1])); n++;
		graph.clear(); graph.resize(n);
		dp.clear(); dp.resize(n,vector<int>(26,-1));
		used.clear(); used.resize(n,0);

		for(auto &i: edges) {
			graph[i[0]].push_back(i[1]);
		}

		for(int i=0;i<n;i++) if(used[i]==0) if(chkCycle(i)) return -1;

		int ans=0;
		for(int j=0;j<26;j++) {
			int mx=0;
			for(int i=0;i<n;i++) {
				mx= max(mx, getDp(i,j));
			}
			// if(mx) cout<<j<<"@"<<mx<<' ';
			ans= max(ans,mx);
		}
		return ans;
	}
};

