class Solution {
public:
    // value[i] stores the color value of subtree with root i and
    // inDegree[i] stores the in-degree of node i
    int ans, value[100005], inDegree[100005];
    vector<int> adj[100005];

    // visited[i] stores if ith node has been visited for current color and
    // inStack[i] stores if ith node has been visited in current dfs
    bool visited[100005], inStack[100005];
    string col;

    int dfs(int node, int color)
    {
        // if we have already visited node, return its value
        if (visited[node])
            return value[node];

        visited[node] = inStack[node] = true;

        if (color == col[node] - 'a')
            value[node]++;

        int val = 0;

        for (auto x : adj[node])
        {
            // cycle exists
            if (inStack[x])
                ans = -1;
            else
                val = max(val, dfs(x, color));
        }

        value[node] += val;
        inStack[node] = false;

        return value[node];
    }

    int largestPathValue(string colors, vector<vector<int>>& edges)
    {
        for (int i = 0; i < 100005; i++)
        {
            inDegree[i] = 0;
            adj[i].clear();
        }

        ans = 0;
        int n = colors.length();
        col = colors;

        for (int i = 0; i < edges.size(); i++)
        {
            inDegree[edges[i][1]]++;
            adj[edges[i][0]].push_back(edges[i][1]);
        }

        for (int j = 0; j < 26; j++)
        {
            for (int i = 0; i < 100005; i++)
            {
                value[i] = 0;
                visited[i] = inStack[i] = false;
            }

            for (int i = 0; i < n; i++)
            {
                // doing dfs only for nodes with indegree 0, equivalent to topological sort
                if (inDegree[i] == 0)
                {
                    int val = dfs(i, j);

                    // cycle exists
                    if (ans == -1)
                        return -1;

                    ans = max(ans, val);
                }
            }
        }

        for (int i = 0; i < n; i++)
        {
            // cycle exists
            if (visited[i] == false)
                return -1;
        }

        return ans;
    }
};
