# Code
```
class Solution {
  int ans = 0;
  boolean[] visited, recStack;
  int[][] maps;
  List<Integer>[] adj;
  
  private boolean dfs(int node, String colors) {
    if (recStack[node]) return true;
    if (visited[node]) return false;

    visited[node] = recStack[node] = true;
    
    for (var neighbor : adj[node]) {
      if (dfs(neighbor, colors))
        return true;

      for (var i=0; i<26; i++)
        maps[node][i] = Math.max(maps[node][i], maps[neighbor][i]);
    }
    recStack[node] = false;
    maps[node][colors.charAt(node) - 'a']++;

    for (var cnt : maps[node])
      ans = Math.max(ans, cnt);

    return false;
  }

  public int largestPathValue(String colors, int[][] edges) {
    var n = colors.length();
    recStack = new boolean[n];
    visited = new boolean[n];
    maps = new int[n][26];
    adj = new ArrayList[n];
  
    for (var i=0; i<n ; i++)
      adj[i] = new ArrayList<>();

    for (var edge : edges)
      adj[edge[0]].add(edge[1]);

    for (var i=0; i<n; i++) {
      if (!visited[i]) {
        Arrays.fill(recStack, false);

        if (dfs(i, colors)) return -1;
      }
    }
    return ans;
  }
}
