class Solution {

      Map<Integer, List<Integer>> graph = new HashMap<>();
      Map<Integer, Integer> indegree = new HashMap<>();
      int res = 0;
      boolean hasCycle = false;
    public int largestPathValue(String colors, int[][] edges) {
        if (edges == null || edges.length == 0) {
			return 1; // no edges, no path, so the max is 1 (assuming colors is not null)
        }
        int n = colors.length(); // node numbered [0, n-1]
        int m = edges.length;

        // init
        for (int[] edge : edges) {
            int start = edge[0];
            int end = edge[1];

            List<Integer> lists = graph.getOrDefault(end, new ArrayList<>());
            graph.put(end, lists);
            lists = graph.getOrDefault(start, new ArrayList<>());
            lists.add(end);
            graph.put(start, lists);

            int ind = indegree.getOrDefault(start, 0);
            indegree.put(start, ind);
            ind = indegree.getOrDefault(end, 0);
            ind++;
            indegree.put(end, ind);
        }

        // dp init
        // dp[x][y] from the start node to node `x`, the max value of color `y`
        int[][] dp = new int[n][26];
        toposort(colors, graph, indegree, dp);

        if (hasCycle) {
            return -1;
        }

        return res;
    }

    private void toposort(String colors, Map<Integer, List<Integer>> graph, Map<Integer, Integer> indegree, int[][] dp) {
        Queue<Integer> queue = new LinkedList<>();
        List<Integer> path = new ArrayList<>();

        for (Map.Entry<Integer, Integer> entry: indegree.entrySet()) {
            if (entry.getValue() == 0) {
                int node = entry.getKey();
                queue.add(node);
                // init dp
                int color = colors.charAt(node) - 'a';
                dp[node][color] = 1;
            }
        }

        while(!queue.isEmpty()) {
            Integer curr = queue.peek(); queue.poll();
            List<Integer> neighbours = graph.getOrDefault(curr, new ArrayList<>());
            for (Integer nb : neighbours) {
                for (int i = 0; i < 26; i++) {
                    // the way to update dp[][] is
                    // if current node color equal to i, then it is the max of my current dp[nb][i] and dp[my prev][i] + 1
                    // if current node color not equal to i, I still need to get the max of my current dp[nb][i] and dp[my prev][i]
                    // b/c my current value can be updated from other path, and got a better path size, but not from my current previous node
                    dp[nb][i] = colors.charAt(nb) - 'a' == i ? Math.max(dp[nb][i], dp[curr][i] + 1) : Math.max(dp[nb][i], dp[curr][i]);
                    res = Math.max(res, dp[nb][i]);
                }

                // decrease indegree
                int ind = indegree.getOrDefault(nb, 0);
                ind--;
                indegree.put(nb, ind);
                if (ind == 0) {
                    queue.add(nb);
                }
            }

            path.add(curr);
        }

        if (path.size() != colors.length()) {
            hasCycle = true;
        }
    }
}
