Class Solution:
    def sort(self, graph):
        n = len(graph)
        indegree = [0] * n
        for node in range(len(graph)):
            for child in graph[node]:
                indegree[child] += 1        
        heads = [i for i, val in enumerate(indegree) if val == 0]        
        queue = heads[:]
        order = []

        while queue:
            node = queue.pop(0)
            order.append(node)
            for child in graph[node]:
                indegree[child] -= 1
                if indegree[child] == 0:
                    queue.append(child)

        if len(order) == n:
            return heads
        else:
            return None


    def getPathValue(self, graph, colors, start):
        color_set = set(colors)
        color_dict = {c: i for i, c in enumerate(color_set)}        
        cache = dict()

        def dfs(node):
            if node in cache:
                return cache[node]
            ans = [0] * len(color_set)
            this_color = colors[node]
            this_color_id = color_dict[this_color]

            for child in graph[node]:
                for i, n in enumerate(dfs(child)):
                    ans[i] = max(n, ans[i])                

            ans[this_color_id] += 1
            cache[node] = tuple(ans)
            return ans

        res = dfs(start)
        return max(res)

    def largestPathValue(self, colors: str, edges: List[List[int]]) -> int:
        # build the graph
        n = len(colors)
        graph = [[] for _ in range(n)]
        for s, e in edges:
            graph[s].append(e)

        # topo sort the graph
        heads = self.sort(graph)
        # if has loop return -1
        if heads is None:
            return -1

        # max value will be one of the starting nodes
        colors = [c for c in colors]
        max_val = 0
        for head in heads:
            val = self.getPathValue(graph, colors, head)
            max_val = max(val, max_val)

        return max_val
