solutions.content:
Steps :
1. Detect cycle in a directed graph, if any cycle found return -1;
2. Since there are only 26 colours, assume at one node keeping a state as the maximum frequency of each color in all of current node's children, including this node as well. The state stored at one node is hence an array of 26(colors) elements. Return this state as result of dfs.
3. Finally count the maximum frequency of colors at each node assuming the node as the starting point of the path.

``` 
class Solution {
public:
    int largestPathValue(string colors, vector<vector<int>>& B) {


        int A = colors.size();
        vector<int> gr[A];

        for(int i=0;i<B.size();i++)
        {
            int src = B[i][0];
            int dest = B[i][1];
            gr[src].push_back(dest);

        }

        vector<bool> visited(A, false);

        bool ans = false;

        set<int> seti;
        for(int i=0;i<A;i++)
        {
            if(visited[i]==false)
                ans |= getans(i, gr, visited, seti);
        }
        if(ans)
            return -1;


        vector<vector<int> > ct(colors.length(), vector<int> (26, 0));
        visited.assign(A, false);

        for(int i=0;i<A;i++)
        {
            if(visited[i])
                continue;
            getdfs(i, gr, ct, colors, visited);
        }


        int ansf = 0;

        for(int i=0;i<A;i++)
        {
            for(int j=0;j<26;j++)
                ansf = max(ansf, ct[i][j]);

        }
        return ansf;
    }

    vector<int> getdfs(int curr, vector<int> gr[], vector<vector<int> >&ct, string &colors, vector<bool> &visited)
    {
        if(visited[curr])
        {
            return ct[curr];
        }

        for(int i=0;i<gr[curr].size();i++)
        {
            vector<int> last = getdfs(gr[curr][i], gr, ct, colors, visited);
            for(int j=0;j<26;j++)
                ct[curr][j] = max(ct[curr][j], last[j]);

        }
        ct[curr][colors[curr]-'a']++;
        visited[curr] = true;

        return ct[curr];

    }

    bool getans(int curr, vector<int> gr[], vector<bool> &visited, set<int> &seti)
    {
