class CycleDetectedException(Exception):
    pass

class Solution:
    def largestPathValue(self, colors: str, edges: List[List[int]]) -> int:
        # adj rep of graph
        n = len(colors)
        g = {}
        for i in range(n):
            g[i] = []
        for src, dst in edges:
            g[src].append(dst)

        # Path tracks cycles, visit to avoid repeat work
        path, visit = set(), set()

        # For each node store the max count of each color
        # over all possible paths from it. Remember this is a DAG
        # {0: {"a": 2, "b": 3, "z": 1}, ....}
        # This is our cache to avoid repeat work
        dp = {i: defaultdict(int) for i in range(n)}

        def dfs(i):
            # Given path has generated a cycle
            if i in path:
                raise CycleDetectedException

            # Don't repeat work if already been through this node
            if i in visit:
                return 0

            dp[i][colors[i]] = 1

            path.add(i)
            for nei in g[i]:
                dfs(nei)
                for c in dp[nei]:
                    neiCount = 1 + dp[nei][c] if c==colors[i] else dp[nei][c]
                    dp[i][c] = max(dp[i][c], neiCount)
            path.remove(i)
            visit.add(i)

        # Try paths starting from each node
        res = 0
        for i in range(len(colors)):
            try:
                dfs(i)
                res = max(res, max(dp[i].values()))
            except CycleDetectedException:
                return -1

        return res

