class Solution:
    def largestPathValue(self, colors: str, edges: List[List[int]]) -> int:
        adj=defaultdict(list)
        for i,j in edges:
            adj[i].append(j)
        n,res=len(colors),0
        visit,path=set(),set()
        count=[[0]*26 for i in range(n)]
        def dfs(node):
            if node in path:
                return float('inf')
            if node in visit:
                return 0
            visit.add(node)
            path.add(node)
            ci=ord(colors[node])-ord('a')
            count[node][ci]=1
            for nei in adj[node]:
                if dfs(nei)==float('inf'):
                    return float('inf')
                for c in range(26):
                    count[node][c]=max(count[node][c],
                    (1 if c==ci else 0) + count[nei][c])
            path.remove(node)
            return max(count[node])
        for i in range(n):
            res=max(dfs(i),res)
        return -1 if res==float('inf') else res

